import React, { useState } from 'react';
import Box from "./componets/box";

const Allcompo = () => {

const [boxStates, setBoxStates] = useState(Array(7).fill(false));

  const handleClick = (index) => {
    const updatedBoxStates = [...boxStates];
    updatedBoxStates[index] = !updatedBoxStates[index];
    setBoxStates(updatedBoxStates);
  };

  const startReverseConversion = () => {
    const delay = 500;
    const reversedBoxStates = [...boxStates].reverse();

    reversedBoxStates.forEach((state, index) => {
      if (state) {
        setTimeout(() => {
          const updatedBoxStates = [...boxStates];
          updatedBoxStates[boxStates.length - index - 1] = false;
          setBoxStates(updatedBoxStates);
        }, delay * (index + 1));
      }
    });
  };

  const allBoxesClicked = boxStates.every((state) => state);

  return (
    <div>
      {boxStates.map((isClicked, index) => (
        <Box
          key={index}
          onClick={() => handleClick(index)}
          isClicked={isClicked}
        />
      ))}
      {allBoxesClicked && startReverseConversion()}
    </div>
  );
};


export default Allcompo;
